for invoice in object:
    amount = 0
    if invoice.type == 'out_invoice':
        for pay_lines in invoice.order_id.payment_advanced_ids:
            amount = amount + pay_lines.currency_id.compute(pay_lines.amount, invoice.currency_id)
        if not invoice.amount_total == 0:
            percent = amount * 100 / invoice.amount_total
        else:
            percent = 0
        if percent < invoice.sale_type_id.percent_min:
            result = True
